import React, {useState} from 'react';
import './Expenses.css';
import ExpenseItem from './ExpenseItem.js';
import Card from './Card';
import ExpensesFilter from './ExpenseFilter.js';

const Expenses = (props) => {
  const [filteredYear, setFilteredYear] = useState('2020');

  const filterChangeHandler = (selectedYear) => {
    setFilteredYear(selectedYear);
  };
/*
let basketballPlayers = students.filter(function (student) {
        return student.sports === "Basketball";
    }).map(function (student) {
        return student.name;
    })
*/
  return (
    <div>
      <Card className='expenses'>
        <ExpensesFilter
          selected={filteredYear}
          onChangeFilter={filterChangeHandler}
        />
        {/* {props.arr.map((expense) => (
          <ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        ))} */}
        {props.arr.filter(function(expense) {
            return filteredYear === expense.date
        }).map(function(expense) {
         <ExpenseItem
         key={expense.id}
         title={expense.title}
         amount={expense.amount}
         date={expense.date}
         />
})};
      </Card>
    </div>
  );
};

export default Expenses;
