import React from "react";
import Button from "./Button";
import Card from "./Card";
import classes from "./ErrorModal.module.css";
//ReactDOM - uses React to use features into the browser
import { ReactDOM } from "react";

const Backdrop = (props) => {
   return <div className={classes.backdrop} onClick={props.onConfirm}/>;
};

const ModalOverlay = (props) => {
    return (
        <Card className={classes.modal}>
            <header className={classes.header}>
            <h2>{props.title}</h2>
            </header>
            <div className={classes.content}>
            <p>{props.message}</p>
            </div>
            <footer className={classes.actions}>
            <Button onClick={props.onConfirm}>Ok</Button>
            </footer>
        </Card>
    );
};
const ErrorModal = (props) => {
  return (
    <React.Fragment>
        {/* ReactDOM.createPortal() takes two inputs:
            1st react node that should be rendered, 
            2nd - is pointer in the container in the real DOM where element should be rendered in */}
        {ReactDOM.createPortal(<Backdrop onConfirm={props.onConfirm} />, document.getElementById('backdrop-root'))}
    </React.Fragment>
  );
};
export default ErrorModal;
