import React, { useState, useRef } from "react";
import Card from "../UI/Card";
import classes from "./AddUser.module.css";
import Button from "../UI/Button";
import ErrorModal from "../UI/ErrorModal";
import Wrapper from "../Helpers/Wrapper";

const AddUser = (props) => {
  //useRef only usable inside functional components
  const nameInputRef = useRef();
  const ageInputRef = useRef();


  const [enteredUserName, setEnteredUserName] = useState("");
  const [enteredAge, setEnteredAge] = useState("");
  const [error, setError] = useState();

  const userNameChangeHandler = (event) => {
    setEnteredUserName(event.target.value);
  };

  const ageChangeHandler = (event) => {
    setEnteredAge(event.target.value);
  };

  const errorHandler = () => {
    setError(null);
  };

  const addUserHandler = (event) => {
    event.preventDefault();
    console.log(nameInputRef);
    if (enteredUserName.trim().length === 0 || enteredAge.trim().length === 0) {
      setError({
          title: 'Invalid input',
          message: 'Please enter a valid name and age (non-empty values)!'
      });
      return;
    }
    //+ does string -> int conversion
    if (+enteredAge < 1) {
        setError({
            title: 'Invalid age',
            message: 'Please enter a valid age (>0)!'
        });
      return;
    }

    // console.log(enteredUserName, enteredAge);
    props.onAddUser(enteredUserName, enteredAge);
    setEnteredAge("");
    setEnteredUserName("");
  };

  return (
    <Wrapper>
      {error && (<ErrorModal title={error.title} message={error.message} onConfirm={errorHandler}/>)}
      <Card className={classes.input}>
        <form onSubmit={addUserHandler}>
          <label htmlFor="username">Username</label>
          {/* //value={enteredUserName} resets the form because of setEnteredAge(''); and setEnteredUserName(''); */}
          <input
            id="username"
            type="text"
            value={enteredUserName}
            onChange={userNameChangeHandler}
            // The first react reaches and render this bit of code, it will set nameInputRef to the native DOM element
            ref={nameInputRef}
          />
          <label htmlFor="age">Age</label>
          <input
            id="age"
            type="number"
            value={enteredAge}
            onChange={ageChangeHandler}
            ref={ageInputRef}
          />
          <Button type="submit">Add User</Button>
        </form>
      </Card>
    </Wrapper>
  );
};
export default AddUser;
